import React from 'react';

import CardInfo from '../components/CardInfo';


function Card(props) {

    return (
        <div class="d-inline-block g-card" onClick={(e) => props.click(props.item)}>
            <img class="g-card-image" src={props.item.imgSrc} alt={props.item.imgSrc} />
            { props.item.selected && <CardInfo title={props.item.title} subTitle={props.item.subTitle} link={props.item.link} />}
        </div>
    );

}

export default Card;
