import React from 'react';

import Card from '../components/Card';

import kristabell from '../images/Kristabell.jpeg';
import github from '../images/github.png';
import linkdn from '../images/LI-In-Bug.png';
import twitter from '../images/twitterlogo.png';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';

class Carousel extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: [
                {
                    id: 0,
                    title: 'Resume',
                    subTitle: 'PDF Format',
                    imgSrc: kristabell,
                    link: 'https://github.com/Kristabell74',
                    selected: false
                },
                {
                    id: 1,
                    title: 'Projects',
                    subTitle: 'GitHub Profile',
                    imgSrc: github,
                    link: 'https://github.com/Kristabell74',
                    selected: false
                },
                {
                    id: 2,
                    title: 'Linkdn',
                    subTitle: 'Networking',
                    imgSrc: linkdn,
                    link: 'https://www.linkedin.com/in/kristabell-johnson-professional-profile/',
                    selected: false
                },
                {
                    id: 0,
                    title: 'Twitter',
                    subTitle: "Let's Tweet",
                    imgSrc: twitter,
                    link: 'https://twitter.com/bell77iam',
                    selected: false
                },

            ]
        }
    }


    handleCardClick = (id, card) => {

        let items = [...this.state.items];

        items[id].selected = items[id].selected ? false : true;

        items.forEach(item => {
            if (item.id !== id) {
                item.selected = false;
            }
        });

        this.setState({
            items
        });
    }


    makeItems = (items) => {
        return items.map(item => {
            return <Card item={item} click={(e => this.handleCardClick(item.id, e))} key={item.id} />
        })
    }


    render() {
        return (
            <Container fluid={true}>
                <Row className="justify-content-around">
                    {this.makeItems(this.state.items)}
                </Row>
            </Container>
        );
    }

}

export default Carousel;
